def npv_decision(npv, decision_type='single'):
    """
    Apply NPV decision rule.

    Args:
        npv (float or list): NPV value(s)
        decision_type (str): 'single', 'independent', or 'mutually_exclusive'

    Returns:
        str or list: Decision(s)
    """
    if decision_type == 'single':
        if npv > 0:
            return 'ACCEPT'
        elif npv < 0:
            return 'REJECT'
        else:
            return 'INDIFFERENT'

    elif decision_type == 'independent':
        return ['ACCEPT' if n > 0 else 'REJECT' for n in npv]

    elif decision_type == 'mutually_exclusive':
        if all(n <= 0 for n in npv):
            return 'REJECT ALL'
        else:
            best_idx = max(range(len(npv)), key=lambda i: npv[i])
            return f'ACCEPT PROJECT {best_idx+1} (NPV = {npv[best_idx]:.2f})'

# Example usage
print(npv_decision(15000, 'single'))  # ACCEPT
print(npv_decision([5000, -2000, 8000], 'independent'))  # ['ACCEPT', 'REJECT', 'ACCEPT']
print(npv_decision([5000, 12000, -2000], 'mutually_exclusive'))  # ACCEPT PROJECT 2
