import numpy as np
from scipy.optimize import brentq

def calculate_npv(cash_flows, discount_rate):
    """Calculate NPV of cash flows."""
    return sum(cf / (1 + discount_rate)**t for t, cf in enumerate(cash_flows))

def calculate_irr(cash_flows):
    """Calculate IRR using numerical methods."""
    def npv_func(r):
        return calculate_npv(cash_flows, r)
    try:
        return brentq(npv_func, -0.99, 10.0)
    except ValueError:
        return None  # No IRR found in range

def compare_projects(project_a, project_b, cost_of_capital):
    """
    Compare two projects using NPV and IRR.

    Returns:
        dict with NPV and IRR for each project and recommendation
    """
    npv_a = calculate_npv(project_a, cost_of_capital)
    npv_b = calculate_npv(project_b, cost_of_capital)
    irr_a = calculate_irr(project_a)
    irr_b = calculate_irr(project_b)

    npv_choice = 'A' if npv_a > npv_b else 'B'
    irr_choice = 'A' if irr_a and irr_b and irr_a > irr_b else 'B'

    return {
        'project_a': {'npv': npv_a, 'irr': irr_a},
        'project_b': {'npv': npv_b, 'irr': irr_b},
        'npv_recommends': npv_choice,
        'irr_recommends': irr_choice,
        'conflict': npv_choice != irr_choice
    }
