def calculate_inflation_rate(cpi_current, cpi_previous):
    """Calculate inflation rate from CPI values."""
    return ((cpi_current - cpi_previous) / cpi_previous) * 100

def real_interest_rate(nominal_rate, inflation_rate):
    """Calculate real interest rate using Fisher equation."""
    return nominal_rate - inflation_rate

def purchasing_power(initial_amount, inflation_rate, years):
    """Calculate purchasing power after given years of inflation."""
    return initial_amount / ((1 + inflation_rate / 100) ** years)

def future_price(current_price, inflation_rate, years):
    """Calculate future price given current price and inflation."""
    return current_price * ((1 + inflation_rate / 100) ** years)

def taylor_rule(r_star, pi_t, pi_star, output_gap):
    """Calculate policy rate using Taylor Rule."""
    return r_star + pi_t + 0.5 * (pi_t - pi_star) + 0.5 * output_gap
