type_signature.scala:
trait Functor[F[_]] {
  def map[A, B](fa: F[A])(f: A => B): F[B]
}

type_signature.typescript:
interface Functor<F> {
  map<A, B>(fa: F<A>, f: (a: A) => B): F<B>
}

type_signature.rust:
trait Functor<A> {
  type Mapped<B>;
  fn fmap<B, F: Fn(A) -> B>(self, f: F) -> Self::Mapped<B>;
}

examples.scala:
// Option is a functor
Some(5).map(_ * 2)  // Some(10)
None.map(_ * 2)     // None

examples.rust:
// Option<T> implements functor via map
Some(5).map(|x| x * 2)  // Some(10)
None.map(|x: i32| x * 2)  // None
